package com.coding.challenge.sliding.window;

import java.util.ArrayList;
import java.util.Arrays;
import java.util.Collections;
import java.util.HashMap;
import java.util.List;
import java.util.Map;

class FindAllAnagrams {
	FindAllAnagrams() {
		throw new AssertionError();
	}

	public static void main(String[] args) {
		final List<Integer> a1 = findAnagrams("cbaebabacd", "abc");
		assert Arrays.asList(0, 6).equals(a1);

		final List<Integer> a2 = findAnagrams("abab", "ab");
		assert Arrays.asList(0, 1, 2).equals(a2);

		final List<Integer> a3 = findAnagrams("abab", "abc");
		assert Collections.emptyList().equals(a3);

		final String s = "";
		final String p = "";
		final List<Integer> a4 = findAnagrams(s, p);
		assert Arrays.asList(0, 10001).equals(a4);

		final List<Integer> a5 = findAnagrams("aabaa", "aa");
		assert Arrays.asList(0, 3).equals(a5);
	}

	static List<Integer> findAnagrams(String s, String p) {
		final int n = s.length();
		final Map<Character, Integer> pf = new HashMap<>();
		for (char ch : p.toCharArray())
			pf.merge(ch, 1, Integer::sum);

		final Map<Character, Integer> wf = new HashMap<>();
		final List<Integer> l = new ArrayList<>();
		int c = 0;
		int i = 0;

		for (int j = 0; j < n; j++) {
			final char jCh = s.charAt(j);
			wf.merge(jCh, 1, Integer::sum);
			if (wf.get(jCh).equals(pf.get(jCh)))
				c = c + 1;
			// fixed window size.
			if (p.length() == j - i + 1) {
				if (c == pf.size())
					l.add(i);
				final char iCh = s.charAt(i);
				if (wf.get(iCh).equals(pf.get(iCh)))
					c = c - 1;
				wf.put(iCh, wf.get(iCh) - 1);
				// maintains our loop-invariant of window size <= length of p.
				i = i + 1;
			}
		}

		return l;
	}
}
